import React, { Component } from "react";
import jsPDF from "jspdf";
import { PureComponent } from "react";

export default class Result extends PureComponent {
  
  // var htmlContent = require('./samplePDF');

  constructor(props) {
    super(props);
    this.state = {};
  }

  imgData = 'data:image/jpeg;base64,'+ Base64.encode('doge.png');

  jsPDFGenerator = () => {
    var doc = new jsPDF();
    doc.addImage(imgData, 20, 20, 150, 150)
    doc.text(20, 100, 'Hello world!');
    doc.addPage();
    doc.text(20, 20, 'Do you like that?');
    
    doc.save('Test.pdf');    
  };

  render() {
    return <button onClick={this.jsPDFGenerator}>Generate PDF</button>;
  }
}
