import React from "react";
import { render } from "@testing-library/react";
import "./App.css";
import UserStore from "./stores/UserStore";
import LoginForm from "./stores/LoginForm";
import InputField from "./stores/InputField.jsx";
import SubmitButton from "./stores/SubmitButton";
import {observer} from 'mobx-react';

class App extends React.Component {
  async componentDidMount() {
    try {
      let res = await fetch(
        "/isLoggedIn", // tu trzeba będzie zrobić endpoint API który to obsłuży
        {
          method: "post",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
        }
      );

      let result = await res.json();
      if (result && result.success) {
        UserStore.loading = false;
        UserStore.isLoggedIn = true;
        UserStore.userName = result.userName;
      } else {
        UserStore.loading = false;
        UserStore.isLoggedIn = false;
      }
    } catch (e) {
      UserStore.loading = false;
      UserStore.isLoggedIn = false;
    }
  }

  async doLogout() {
    try {
      let res = await fetch(
        "/logout", // tu trzeba będzie zrobić endpoint API który to obsłuży
        {
          method: "post",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
        }
      );

      let result = await res.json();
      if (result && result.success) {
        UserStore.isLoggedIn = false;
        UserStore.userName = "";
      }
    } catch (e) {
      console.log(e);
    }
  }

  render() {
    if (UserStore.loading) {
      return (
        <div className="app">
          <div className="container">Loading please wait</div>
        </div>
      );
    } 

    else {
      if (UserStore.isLoggedIn) {
        <div className="app">
          <div className="container">
            Welcome {UserStore.userName}
            <SubmitButton
              text={"Log out"}
              disabled={false}
              onClick={() => this.doLogout}
            ></SubmitButton>
          </div>
        </div>

        return (
          <div className="app">
            <div className="container">
              <LoginForm />
            </div>
          </div>
        );
      }
    }

    return <div className="app"></div>;
  }
}

export default observer(App);
